// IntroSection.js
import React from 'react';
import { Carousel } from 'react-bootstrap';

const IntroSection = () => (
  <div className="position-relative">
    {/* Carousel as the Background */}
    <Carousel className="my-5" interval={3000} pause="hover">
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://shorturl.at/EBBgt"
          alt="First slide"
          style={{ height: '60vh', objectFit: 'cover' }} // Reduced height for smaller carousel
        />
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://shorturl.at/i26zF"
          alt="Second slide"
          style={{ height: '60vh', objectFit: 'cover' }}
        />
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://shorturl.at/ARR44"
          alt="Third slide"
          style={{ height: '60vh', objectFit: 'cover' }}
        />
      </Carousel.Item>
    </Carousel>

    {/* Welcome Message Overlay with opacity */}
    <div className="position-absolute top-50 start-50 translate-middle text-center text-light" style={{ maxWidth: '80%' }}>
      {/* Semi-transparent background to improve text visibility */}
      <div className="position-absolute top-0 start-0 w-100 h-100" style={{
        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Dark overlay with opacity
        zIndex: -1, // Ensure the overlay is behind the text
      }}></div>

      <h1 className="display-4">Welcome to Our Website!</h1> {/* Smaller heading */}
      <p className="lead">We're excited to have you here. Explore and enjoy our products!</p>
      <a href="#explore" className="btn btn-primary btn-lg">Get Started</a>
    </div>
  </div>
);

export default IntroSection;
